function apply(text, fontMap) {
  if (!text || text.length === 0) {
    return "";
  }

  return text.replace(/[a-zA-Z0-9]/g, (char) => {
    const fonty = fontMap[char];
    return fonty ? fonty : char;
  });
}
    
